import { Book } from '../shared/Book.model'; //import { Subject } from 'rxjs/Subject';

import { Subject } from 'rxjs'; // User defined service to maintain Book list

export class BookShoppingListService {
  constructor() {
    this.BooksChanged = new Subject();
    this.startedEditing = new Subject(); // List of books which displays always

    this.Books = [new Book('Practical Approach in C - Ajay Mittal', 580), new Book('Linux System Programming - Robert Love', 710), new Book('Windows Internals - Soloman', 1150), new Book('Programming Windows- Petzold', 980), new Book('Computer Architecture - V. Rajaraman', 410)];
  } // Returns all books


  getBooks() {
    return this.Books.slice();
  } // Return specific book


  getBook(index) {
    return this.Books[index];
  } // Add new book


  addBook(Book) {
    this.Books.push(Book);
    this.BooksChanged.next(this.Books.slice());
  } // Add multiple books


  addBooks(Books) {
    this.Books.push(...Books);
    this.BooksChanged.next(this.Books.slice());
  } // Update book record


  updateBook(index, newBook) {
    this.Books[index] = newBook;
    this.BooksChanged.next(this.Books.slice());
  } // Delete existing book


  deleteBook(index) {
    this.Books.splice(index, 1);
    this.BooksChanged.next(this.Books.slice());
  }

}
